private void ButtonAttributionTemplate_Click(object sender, System.Windows.RoutedEventArgs e) { // Get the ArcGISTiledMapServiceLayer. ESRI.ArcGIS.Client.ArcGISTiledMapServiceLayer myArcGISTiledMapServiceLayer = null; myArcGISTiledMapServiceLayer = (ESRI.ArcGIS.Client.ArcGISTiledMapServiceLayer)(Map1.Layers[0]);
// Display the ArcGISTiledMapServiceLayer.CopyrightText in a TextBox. TextBox1.Text = myArcGISTiledMapServiceLayer.CopyrightText;
// Display the ArcGISTiledMapServiceLayer.CopyrightText information via the ESRI Attribution Control. Attribution1.Layers = Map1.Layers;
// Display the ArcGISTiledMapServiceLayer.CopyrightText information via a Microsoft ContentControl. ContentControl1.ContentTemplate = myArcGISTiledMapServiceLayer.AttributionTemplate; ContentControl1.Content = myArcGISTiledMapServiceLayer;
// Display the ArcGISTiledMapServiceLayer.CopyrightText information via a Button. Button1.ContentTemplate = myArcGISTiledMapServiceLayer.AttributionTemplate; Button1.Content = myArcGISTiledMapServiceLayer; }
